function getDayOfYear(date) {
  return Math.floor((date - new Date(date.getFullYear(), 0, 0)) / 86400000);
}

function GetClock(){
    var d=new Date();
    var nhour=d.getHours(),nmin=d.getMinutes(),nday=d.getDay(),nmonth=d.getMonth(),ndate=d.getDate(),nyear=d.getFullYear();
    var ampm = nhour >= 12 ? 'PM' : 'AM'; // Determine AM or PM
    var hour12 = nhour % 12 || 12; // Convert to 12-hour format
    if(nmin<=9) nmin="0"+nmin;
    // Get today's date
    var today = new Date();
    // Calculate the day of the year
    var dayOfYear = getDayOfYear(today);
    dayOfYear -= 1;
    var lmfao = heathen_date[dayOfYear];
    var clocktext=""+hour12+":"+nmin+" "+ampm+" "+tday[nday]+", "+ndate+" "+tmonth[nmonth]+" "+nyear+" ";
    document.getElementById('clockwrite').innerHTML=clocktext;
    document.getElementById('calendarwrite').innerHTML=lmfao;

}

